import React, { useState, useEffect } from 'react';
import { welfareAPI, seosanAPI, cultureAPI } from '../api/backend.api';
import { testAllAPIs } from '../api/test-all-apis';
import './ApiTest.css';

const ApiTest = () => {
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [data, setData] = useState(null);
  const [selectedCategory, setSelectedCategory] = useState('WELFARE_SENIOR');
  const [region, setRegion] = useState('ÎåÄÏÇ∞Ïùç');
  const [page, setPage] = useState(0);
  const [size, setSize] = useState(5);
  const [testResults, setTestResults] = useState(null);

  const categoryOptions = [
    { value: 'WELFARE_SENIOR', label: 'Î≥µÏßÄ-Ïñ¥Î•¥Ïã†', api: () => welfareAPI.getElderly(region, page, size) },
    { value: 'WELFARE_DISABLED', label: 'Î≥µÏßÄ-Ïû•Ïï†Ïù∏', api: () => welfareAPI.getDisabled(region, page, size) },
    { value: 'WELFARE_WOMEN_FAMILY', label: 'Î≥µÏßÄ-Ïó¨ÏÑ±Í∞ÄÏ°±', api: () => welfareAPI.getWomenFamily(region, page, size) },
    { value: 'WELFARE_CHILD_YOUTH', label: 'Î≥µÏßÄ-ÏïÑÎèôÏ≤≠ÏÜåÎÖÑ', api: () => welfareAPI.getChildYouth(region, page, size) },
    { value: 'WELFARE_YOUTH', label: 'Î≥µÏßÄ-Ï≤≠ÎÖÑ', api: () => welfareAPI.getYouth(region, page, size) },
    { value: 'HEALTH_WELLNESS', label: 'Î≥¥Í±¥/Í±¥Í∞ï', api: () => seosanAPI.getHealth(region, page, size) },
    { value: 'NOTICE', label: 'Í≥µÏßÄÏÇ¨Ìï≠', api: () => seosanAPI.getNotices(region, page, size) },
    { value: 'PRESS_RELEASE', label: 'Î≥¥ÎèÑÏûêÎ£å', api: () => seosanAPI.getPressRelease(region, page, size) },
    { value: 'CULTURE_NEWS', label: 'Î¨∏ÌôîÏÜåÏãù', api: () => cultureAPI.getCultureNews(region, page, size) },
    { value: 'CITY_TOUR', label: 'ÏãúÌã∞Ìà¨Ïñ¥', api: () => cultureAPI.getCityTour(region, page, size) },
    { value: 'TOUR_GUIDE', label: 'Í¥ÄÍ¥ë/ÏïàÎÇ¥', api: () => cultureAPI.getTourGuide(region, page, size) },
  ];

  const fetchData = async () => {
    setLoading(true);
    setError(null);

    try {
      const selectedOption = categoryOptions.find(opt => opt.value === selectedCategory);
      console.log('ÏÑ†ÌÉùÎêú Ïπ¥ÌÖåÍ≥†Î¶¨:', selectedCategory);
      console.log('ÏÑ†ÌÉùÎêú ÏòµÏÖò:', selectedOption);

      if (selectedOption) {
        const result = await selectedOption.api();
        console.log('API ÏùëÎãµ Í≤∞Í≥º:', result);
        setData(result);
      }
    } catch (err) {
      setError(err.message);
      console.error('API Ìò∏Ï∂ú Ïò§Î•ò:', err);
    } finally {
      setLoading(false);
    }
  };

  const runAllTests = async () => {
    setLoading(true);
    setError(null);
    setTestResults(null);

    try {
      const results = await testAllAPIs();
      setTestResults(results);
    } catch (err) {
      setError(err.message);
      console.error('Ï†ÑÏ≤¥ ÌÖåÏä§Ìä∏ Ïò§Î•ò:', err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchData();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [selectedCategory, region, page, size]);

  return (
    <div className="api-test-container">
      <h2>API ÌÖåÏä§Ìä∏ ÌéòÏù¥ÏßÄ</h2>

      <div className="controls">
        <div className="control-group">
          <label>Ïπ¥ÌÖåÍ≥†Î¶¨:</label>
          <select value={selectedCategory} onChange={(e) => setSelectedCategory(e.target.value)}>
            {categoryOptions.map(opt => (
              <option key={opt.value} value={opt.value}>{opt.label}</option>
            ))}
          </select>
        </div>

        <div className="control-group">
          <label>ÏßÄÏó≠:</label>
          <input 
            type="text" 
            value={region} 
            onChange={(e) => setRegion(e.target.value)}
          />
        </div>

        <div className="control-group">
          <label>ÌéòÏù¥ÏßÄ:</label>
          <input 
            type="number" 
            value={page} 
            onChange={(e) => setPage(Number(e.target.value))}
            min="0"
          />
        </div>

        <div className="control-group">
          <label>ÏÇ¨Ïù¥Ï¶à:</label>
          <input 
            type="number" 
            value={size} 
            onChange={(e) => setSize(Number(e.target.value))}
            min="1"
            max="20"
          />
        </div>

        <button onClick={fetchData} disabled={loading}>
          {loading ? 'Î°úÎî©Ï§ë...' : 'Îã§Ïãú Ï°∞Ìöå'}
        </button>

        <button 
          onClick={runAllTests} 
          disabled={loading}
          style={{ 
            backgroundColor: '#ff6b6b',
            marginLeft: '10px'
          }}
        >
          {loading ? 'ÌÖåÏä§Ìä∏Ï§ë...' : 'üß™ Ï†ÑÏ≤¥ API ÌÖåÏä§Ìä∏'}
        </button>
      </div>

      <div className="api-url">
        <strong>API URL:</strong> 
        <code>
          {process.env.REACT_APP_BACKEND_URL || 'http://localhost:8083'}/api/posts?category={'{selectedCategory}'}&region={'{region}'}&page={'{page}'}&size={'{size}'}
        </code>
      </div>

      {error && (
        <div className="error">
          <h3>Ïò§Î•ò Î∞úÏÉù:</h3>
          <p>{error}</p>
        </div>
      )}

      {loading && <div className="loading">Îç∞Ïù¥ÌÑ∞ Î°úÎî©Ï§ë...</div>}

      {!loading && data && (
        <div className="result">
          <h3>Ï°∞Ìöå Í≤∞Í≥º:</h3>
          <pre>{JSON.stringify(data, null, 2)}</pre>
        </div>
      )}

      {!loading && testResults && (
        <div className="test-results" style={{ marginTop: '20px' }}>
          <h3>üß™ Ï†ÑÏ≤¥ API ÌÖåÏä§Ìä∏ Í≤∞Í≥º</h3>
          <div style={{ 
            display: 'grid', 
            gridTemplateColumns: 'repeat(auto-fill, minmax(300px, 1fr))',
            gap: '10px',
            marginTop: '15px'
          }}>
            {testResults.map((result, index) => (
              <div 
                key={index}
                style={{
                  padding: '10px',
                  border: '1px solid #ddd',
                  borderRadius: '4px',
                  backgroundColor: result.status === 'success' ? '#e8f5e9' : '#ffebee'
                }}
              >
                <span style={{ 
                  fontSize: '18px',
                  marginRight: '8px'
                }}>
                  {result.status === 'success' ? '‚úÖ' : '‚ùå'}
                </span>
                <strong>{result.name}</strong>
                {result.status === 'failed' && (
                  <div style={{ 
                    fontSize: '12px',
                    color: '#d32f2f',
                    marginTop: '5px'
                  }}>
                    {result.error}
                  </div>
                )}
              </div>
            ))}
          </div>
          <div style={{ 
            marginTop: '20px',
            padding: '15px',
            backgroundColor: '#f5f5f5',
            borderRadius: '4px'
          }}>
            <strong>ÏöîÏïΩ:</strong>
            <div>
              ‚úÖ ÏÑ±Í≥µ: {testResults.filter(r => r.status === 'success').length}Í∞ú | 
              ‚ùå Ïã§Ìå®: {testResults.filter(r => r.status === 'failed').length}Í∞ú | 
              üìä Ï†ÑÏ≤¥: {testResults.length}Í∞ú
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ApiTest;
